                            "thumbnail": "https://imageproxy-prod.ent.sdy.ai/v1/image/500x/https://fws.weforum.org/images/topics/a1G0X000005JLTqUAO/standard",
                            "medium": "https://imageproxy-prod.ent.sdy.ai/v1/image/1500x/https://fws.weforum.org/images/topics/a1G0X000005JLTqUAO/standard",
